import { useState } from "react";
export default function Board() {
  let temp = Array(3).fill(null).map( () => Array(3).fill(null));
  const [boardArray, setBoardArray] = useState(temp);
  const players = ["X", "O"];
  const [player, setPlayer] = useState("X");
  const [playerState, setPlayerState] = useState("Current")

  function handleclick(i, j) {
    if (boardArray[i][j] != null || playerState != "Current") return;

    let newboard = boardArray.slice();
    newboard[i][j] = player;
    setBoardArray(newboard);
    if (calcWinner(i, j) === true) {
      setPlayerState("Winner !!!");
    };
    if (playerState != "Winner !!!!!") {
      (player === "X") ? setPlayer("O") : setPlayer("X");
    }
  }

  function calcWinner(row, col) {
    let result = false;
    let curr = boardArray[row][col];
    let size = boardArray.length;
    let check = 0
    for (let i = 0; i < size; i++) {
      if (curr === boardArray[row][i]) {
        check++;
        if (check == size) return (result = true);
      }
      else {
        check = 0;
        break;
      }
    }

    for (let i = 0; i < size; i++) {
      if (curr === boardArray[i][col]) {
        check++;
        if (check == size) return (result = true);
      }
      else {
        check = 0;
        break;
      }
    }

    if (row === col) {
      for (let i = 0; i < size; i++) {
        if (curr === boardArray[i][i]) {
          check++;
          if (check == size) return (result = true);
        }
        else {
          check = 0;
          break;
        }
      }
    }

    if (col === size - row - 1) {
      for (let i = 0; i < size; i++) {
        if (curr === boardArray[i][size - i - 1]) {
          check++;
          if (check == size) return (result = true);
        }
        else {
          check = 0;
          break;
        }
      }
    }

    return result;
  }

  function displayWinner() {
    setPlayerState("Winner");
  }

  function resetBoard() {
    setBoardArray(temp);
    setPlayer("X");
    setPlayerState("Current");
  }

  return(
    <div>
      <h3>{playerState} : {player}</h3>
      {Array(3).fill(null).map( (_,i) => (
        <Row rowNum={i} rowArray={boardArray[i]} onClick={handleclick} />
      ))}

      <button onClick={resetBoard}>New Game</button>
    </div>
  );
}

//i = row, j = col
function Row({rowNum, rowArray, onClick}) {
  return (
    <div className="board-row">
      {Array(3).fill(null).map( (_,i) =>
        <Square rowNum={rowNum} colNum={i} value={rowArray[i]} onClick={onClick} />
      )}
    </div>
  );
}

function Square({rowNum, colNum, value, onClick}) {
  return (
    <button className="square" onClick={() => onClick(rowNum, colNum)}>
      {value}
    </button>
  );
}
