import { useState } from "react";
export default function Game() {
  let temp = Array(3).fill(null).map( () => Array(3).fill(null));
  const [boardArray, setBoardArray] = useState(temp);
  const players = ["X", "O"];
  const [player, setPlayer] = useState("X");
  const [playerState, setPlayerState] = useState("Current")
  const size = 3;

  function handleclick(i, j) {
    if (boardArray[i][j] != null || playerState != "Current") return;

    let newboard = boardArray.slice();
    newboard[i][j] = player;
    setBoardArray(newboard);

    if (calcWinner(i, j) === true) setPlayerState("Winner !!!");

    else (player === "X") ? setPlayer("O") : setPlayer("X");
  }

  function calcWinner(row, col) {
    let curr = boardArray[row][col];
    let size = boardArray.length;
    let check = 0
    let rowWin, colWin, diag1Win, diag2Win;
    rowWin = colWin = diag1Win = diag2Win = true;
    for (let i = 0; i < size; i++) {
      if (curr !== boardArray[row][i]) rowWin = false;
      if (curr !== boardArray[i][col]) colWin = false;
      if (curr !== boardArray[i][i]) diag1Win = false;
      if (curr !== boardArray[i][size - i - 1]) diag2Win = false;

      if (!(rowWin || colWin || diag1Win || diag2Win)) return false;
    }

    return  rowWin || colWin || diag1Win || diag2Win;
  }

  function displayWinner() {
    setPlayerState("Winner");
  }

  function resetBoard() {
    setBoardArray(temp);
    setPlayer("X");
    setPlayerState("Current");
  }

  function boardToPrev() {

  }

  return(
    <div>
      <h3 id="playerState">{playerState} : {player}</h3>
      <Board size={size} boardArray={boardArray} onClick={handleclick} />

    </div>
  );
}

function Board({ size, boardArray, onClick }) {
  {
    Array({size}).fill(null).map((_, i) => (
      <Row size={size} rowNum={i} rowArray={boardArray[i]} onClick={onClick} />
    ))
  }
}

//i = row, j = col
function Row({size, rowNum, rowArray, onClick }) {
  return (
    <div className="board-row">
      {Array({size}).fill(null).map( (_,i) =>
        <Square rowNum={rowNum} colNum={i} value={rowArray[i]} onClick={onClick} />
      )}
    </div>
  );
}

function Square({rowNum, colNum, value, onClick}) {
  return (
    <button className="square" onClick={() => onClick(rowNum, colNum)}>
      {value}
    </button>
  );
}
