import { use, useState } from "react"


export default function GameHistory({ HistoryArray, setBoard, setPlayer, setPlayerState, players }) {
    const [hide, setHide] = useState(-1);
    let tmp = HistoryArray[HistoryArray.length-1];
    let currBoard = [];
    let nextPlayer;
    if (tmp) { 
        currBoard = tmp.board; 
        nextPlayer = (tmp.player + 1) % players.length;
    }

    function toggleBoard(historyIdx) {
        let button = document.querySelector(`button.update[data-key="${i}"]`);

        if (button.innerHTML === hide) {
            setBoard(currBoard);
            setPlayer(nextPlayer);
            setPlayerState("Next Player");
        }
        else {
            button.innerHTML = "hide";
            setBoard(HistoryArray[historyIdx].board);
            setPlayer(HistoryArray[historyIdx].player);
            setPlayerState("Last Player");
        }
    }

    function log(i) {
        console.log(document.querySelector(`button.update[data-key="${i}"]`).innerHTML);
    }

    return (
        <ol>
            {
                Array(HistoryArray.length).fill(null).map(
                    (_,i) => (
                        <li key={i+1}>
                            game {i+1} 
                            <button className="show" data-key={i} onClick={() => (toggleBoard(i))} >
                                {(hide === i) ? "hide" : "show"}
                            </button>
                            <button className="update" data-key={i+1} onClick={ () => (log(i+1))}>update</button>
                        </li>
                    )
                )
            }
        </ol>
    );
}
