import { useState } from "react";
export default function Board() {
  let temp = Array(3).fill(null).map( () => Array(3).fill(null));
  const [boardArray, setBoardArray] = useState(temp);
  const players = ["X", "O"];
  const [player, setPlayer] = useState("X");
  const [playerType, setPlayerType] = useState("Current");

  function handleclick(i, j) {
    if (boardArray[i][j] != null) return;

    let newboard = boardArray.slice();
    newboard[i][j] = player;
    setBoardArray(newboard);
    (player === "X") ? setPlayer("O") : setPlayer("X");
  }


  return(
    <div>
      <h3>{playerType} : {player}</h3>
      {Array(3).fill(null).map( (_,i) => (
        <Row row={i} rowArray={boardArray[i]} onClick={handleclick} />
      ))}
    </div>
  );
}

//i = row, j = col
function Row({row, rowArray, onClick}) {
  return (
    <div className="board-row">
      {Array(3).fill(null).map( (_,i) =>
        <Square row={row} col={i} value={rowArray[i]} onClick={onClick} />
      )}
    </div>
  );
}

function Square({row, col, value, onClick}) {
  return (
    <button className="square" onClick={() => onClick(row, col)}>
      {value}
    </button>
  );
}
