import { use, useState } from "react"


export default function GameHistory({ HistoryArray, setBoard, setPlayer, setPlayerState }) {
    const [hide, setHide] = useState(-1);
    let tmp = HistoryArray[HistoryArray.length-1];
    let currBoard = [];

    if (tmp) {
        currBoard = tmp.board;
    }

    function toggleBoard(historyIdx) {
        if (hide === historyIdx) {
            let lastPlayer = HistoryArray[historyIdx].player;
            setHide(-1);
            setBoard(currBoard);
            setPlayer((lastPlayer + 1) % players)
        }

        setHide(historyIdx);
        setBoard(HistoryArray[historyIdx].board);
        setPlayer(HistoryArray[historyIdx].player);
        setPlayerState("Last Player");
    }

    return (
        <ol>
            {
                Array(HistoryArray.length).fill(null).map(
                    (_,i) => (
                        <li key={i+1}>
                            game {i+1} 
                            <button key={i+1} onClick={() => (toggleBoard(i))} >
                                {(hide === i) ? "hide" : "show"}
                            </button>
                        </li>
                    )
                )
            }
        </ol>
    );
}
